/**
 * Fetch Gemini file status and route the flow.
 *
 *  â–¸ OUTPUT 1 â†’ msg while state is PROCESSING  or  SUCCEEDED
 *  â–¸ OUTPUT 2 â†’ msg when state is FAILED  or any exception occurs
 *
 *  Prerequisites:
 *    â€¢ External modules enabled in Node-RED (functionExternalModules: true)
 *    â€¢ npm install @google/generative-ai   (already declared in the node)
 *    â€¢ msg.apiKey  contains your Google API key
 */
// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
// 0.  Make sure we really have an API key and build the helper
// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
if (!msg.apiKey || typeof msg.apiKey !== "string") {
  node.error("Missing or invalid msg.apiKey", msg);
  return [ null, msg ];                 // error output (#2)
}

const { GoogleAIFileManager } = googleGenerativeAiServer;
const fileManager = new GoogleAIFileManager(msg.apiKey);   // ðŸ‘ˆ pass the *string*

// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
// 1. Resolve the resource name (â€œfiles/abc123â€¦â€)
// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
let resourceName =
        msg.uploadResponse?.file?.name ??                         // canonical
        msg.gemini?.upload?.uploadResponse?.file?.name ??         // older path
        msg.uploadResponse?.fileName ??                           // legacy
        msg.fileName ??                                           // manual override
        null;

// Fallback: extract â€œâ€¦/v1beta/**files/xxx**â€ from a URI, if thatâ€™s all we have
if (!resourceName) {
    const uri =
          msg.uploadResponse?.file?.uri ??
          msg.gemini?.upload?.uploadResponse?.file?.uri;
    if (uri) {
        const parts = uri.split("/v1beta/");
        if (parts.length === 2) resourceName = parts[1];
    }
}

// Abort if still nothing
if (!resourceName) {
    node.error("Fetch Upload Status: cannot find Gemini file resource name", msg);
    return [ null, msg ];          // send to error output (#2)
}

// Normalise: ensure it starts with â€œfiles/â€
if (!resourceName.startsWith("files/")) {
    resourceName = `files/${resourceName}`;
}

// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
// 2. Query Gemini  (return a Promise so Node-RED waits)
// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
return fileManager.getFile(resourceName)
  .then(file => {
      // expose full response downstream
      msg.file = file;

      // record progress for UI / logs
      msg.uploadResponse = msg.uploadResponse || {};
      msg.uploadResponse.status = file.state;      // PROCESSING | SUCCEEDED | FAILED

      switch (file.state) {
          case "FAILED":
              node.error("Gemini processing FAILED", msg);
              return [ null, msg ];                // error output (#2)

          case "PROCESSING":
          case "SUCCEEDED":
              return [ msg, null ];                // success output (#1)

          default:
              node.warn(`Unexpected file state: ${file.state}`, msg);
              return [ null, msg ];                // treat unknown as error
      }
  })
.catch(err => {
    // SDK bug: 500 + â€œFailed to convert server response to JSONâ€
    // really means the file endpoint isnâ€™t ready yet.
    const msgTxt = err.message || "";
    if (msgTxt.includes("Failed to convert server response to JSON")) {
        msg.uploadResponse = msg.uploadResponse || {};
        msg.uploadResponse.status = "PROCESSING";   // pretend itâ€™s still working
        return [ msg, null ];                       // success output (#1)
    }

    node.error(`Gemini SDK error: ${msgTxt}`, msg);
    return [ null, msg ];                           // error output (#2)
});

