return new Promise((resolve, reject) => {
    node.status({ fill: "green", shape: "dot", text: "â¬‡ï¸ Downloading..." });

    const file = fs.createWriteStream(path);

    https.get(url, (response) => {
        if (response.statusCode !== 200) {
            msg.statusInfo = `âŒ Failed to download file. Status code: ${response.statusCode}`;
            msg.statusCode = response.statusCode;
            node.status({ fill: "red", shape: "ring", text: `âŒ Status ${response.statusCode}` });
            return resolve([null, msg]);
        }

        response.pipe(file);

        file.on("finish", () => {
            file.close(() => {
                msg.statusInfo = `âœ… File saved to ${path}`;
                msg.filePath = path;
                msg.statusCode = 200;
                node.status({ fill: "green", shape: "dot", text: "âœ… Download complete" });
                resolve([msg, null]);
            });
        });
    }).on("error", (err) => {
        try { fs.unlinkSync(path); } catch (e) {} // Clean up if file partially exists
        msg.statusInfo = `âŒ Error downloading file: ${err.message}`;
        msg.statusCode = 400;
        node.status({ fill: "red", shape: "ring", text: "âŒ Download error" });
        resolve([null, msg]);
    });
});

