// Construct Gemini File GET URL with API key
const baseUrl = "https://generativelanguage.googleapis.com/v1beta/";
const filename = msg.filename;
const apiKey = msg.apiKey;

try {
    // Validate required inputs
    if (!filename || !apiKey) {
        throw new Error("Missing required parameters: filename or apiKey");
    }

    // Build the GET request
    msg.method = "GET";
    msg.url = `${baseUrl}${filename}?key=${apiKey}`;
    msg.headers = {
        "Accept": "application/json"
    };

    // Indicate success and send to the first output
    msg.statusCode = 200;
    return [msg, null];
} catch (error) {
    // Handle errors, such as missing parameters
    msg.payload = `âŒ Error building GET request: ${error.message}`;
    msg.statusCode = 400;
    node.error(msg.payload, msg);
    return [null, msg]; // Send to the second output for errors
}

const fileState = msg.gemini.getFile.state;

switch (fileState) {
    case "PROCESSING":
        msg.statusCode = 102; // Processing
        msg.statusInfo = "PROCESSING";
        return [msg, null, null, null];
    case "ACTIVE":
        msg.statusCode = 200; // OK
        msg.statusInfo = "ACTIVE";
        return [null, msg, null, null];
    case "FAILED":
        msg.statusCode = 500; // Server Error
        msg.statusInfo = "FAILED";
        return [null, null, msg, null];
    case "STATE_UNSPECIFIED":
    default:
        msg.statusCode = 400; // Bad Request / Unknown
        msg.statusInfo = "STATE_UNSPECIFIED";
        return [null, null, null, msg];
}

const filePath = msg.filePath;
const apiKey = msg.apiKey;
const url = new urlLib.URL(`https://generativelanguage.googleapis.com/upload/v1beta/files?key=${apiKey}`);

// Progress tracking
let totalSize = fs.statSync(filePath).size;
let uploaded = 0;

const readStream = fs.createReadStream(filePath);
const options = {
    method: "POST",
    headers: {
        "Content-Type": "video/mp4",
        "Content-Length": totalSize
    }
};

const req = https.request(url, options, (res) => {
    let response = "";

    res.on("data", chunk => response += chunk);

    res.on("end", () => {
        try {
            msg.payload = JSON.parse(response);
        } catch (err) {
            msg.payload = response;
            msg.parseError = true;
        }

        msg.statusCode = res.statusCode;
        node.status({ fill: "green", shape: "dot", text: `âœ… Upload complete` });
        node.send([msg, null]);
    });
});

req.on("error", (err) => {
    msg.payload = `âŒ Upload failed: ${err.message}`;
    msg.statusCode = 500;
    node.status({ fill: "red", shape: "ring", text: "âŒ Upload error" });
    node.error(msg.payload, msg);
    node.send([null, msg]);
});

readStream.on("data", (chunk) => {
    uploaded += chunk.length;
    let percent = Math.round((uploaded / totalSize) * 100);
    node.status({ fill: "blue", shape: "dot", text: `Uploading: ${percent}%` });
});

readStream.pipe(req);
return null;
