// Retrieve secrets from global context (Vault)
var GeminiApiKey = global.get("SECRETS.GEMINI_API_KEY");
var GeminiModel = global.get("SECRETS.GEMINI_MODEL");
var IconikBaseUrl = global.get("SECRETS.ICONIK_BASE_URL");
var IconikPassword = global.get("SECRETS.ICONIK_PASSWORD");
var IconikUsername = global.get("SECRETS.ICONIK_USERNAME");

// Check for each required secret individually
if (!GeminiApiKey) {
    node.error("âŒ Global.SECRETS.GEMINI_API_KEY is not set. Please Check Space SECRET");
    msg.error = "Global.SECRETS.GEMINI_API_KEY is not set. Please Check Space SECRET";
    msg.statusCode = 404;
    return [null, msg]; 
}

if (!GeminiModel) {
    node.error("âŒ Global.SECRETS.GEMINI_MODEL is not set. Please Check Space SECRET");
    msg.error = "Global.SECRETS.GEMINI_MODEL is not set. Please Check Space SECRET";
    msg.statusCode = 404;
    return [null, msg];
}

if (!IconikBaseUrl) {
    node.error("âŒ Global.SECRETS.ICONIK_BASE_URL is not set. Please Check Space SECRET");
    msg.error = "Global.SECRETS.ICONIK_BASE_URL is not set. Please Check Space SECRET";
    msg.statusCode = 404;
    return [null, msg];
}

if (!IconikPassword) {
    node.error("âŒ Global.SECRETS.ICONIK_PASSWORD is not set. Please Check Space SECRET");
    msg.error = "Global.SECRETS.ICONIK_PASSWORD is not set. Please Check Space SECRET";
    msg.statusCode = 404;
    return [null, msg];
}

if (!IconikUsername) {
    node.error("âŒ Global.SECRETS.ICONIK_USERNAME is not set. Please Check Space SECRET");
    msg.error = "Global.SECRETS.ICONIK_USERNAME is not set. Please Check Space SECRET";
    msg.statusCode = 404;
    return [null, msg];
}

// Construct the message secret
msg.secret = {
    "GeminiApiKey": GeminiApiKey,
    "GeminiModel": GeminiModel,
    "IconikBaseUrl": IconikBaseUrl,
    "IconikPassword": IconikPassword,
    "IconikUsername": IconikUsername
};

msg.statusCode = 200;

return [msg, null]; // All good, send to output 1

// Define supported media types for images and videos
const supportedMediaTypes = {
  image: ["image/jpeg", "image/png", "image/webp", "image/heic", "image/heif"],
  video: ["video/mp4", "video/mpeg", "video/mov", "video/avi", "video/x-flv", "video/mpg", "video/webm", "video/wmx", "video/3gpp"]
};

// Extract the media type from the incoming message
const assetMediaType = msg.mediaType;

// Function to check if the media type is supported
const isSupportedMediaType = (type, mediaType) => supportedMediaTypes[type].includes(mediaType);

try {
  // Validate the media type
  if (!isSupportedMediaType('image', assetMediaType) && !isSupportedMediaType('video', assetMediaType)) {
    // If unsupported, set the error message and supported media types
    msg.statusInfo = `The selected asset has a media type of "${assetMediaType}", which is not among the supported media types. Please choose a compatible media type.`;
    msg.supportedMediaTypes = supportedMediaTypes;
    msg.statusCode = 400; // Set status code for unsupported media type
    return [null, msg]; // Send to output 2
  } else {
    // If supported, set a success message
    msg.statusInfo = `The selected asset has a supported media type: "${assetMediaType}".`;
    msg.statusCode = 200; // Set status code for supported media type
    return [msg, null]; // Send to output 1
  }
} catch (error) {
  // Handle unexpected errors
  node.error("An error occurred while checking the media type", msg);
  msg.statusInfo = "An internal error occurred.";
  msg.statusCode = 500; // Set status code for internal error
  return [null, msg]; // Send to output 2
}
