const selectedOptions = msg.selectedOptions;
const outputFormats = {};

// Check for Object Detection
if (selectedOptions.includes("Highlights")) {
  const highlightsFormat = [
    {
      "highlight": "[Brief description of the important moments]",
      "timestamp": "[Timestamp for the start and end time of the highlight in the format HH:MM:SS]",
      "duration": "Duration of the highlight in the video"
    }
  ];
  outputFormats.highlights = highlightsFormat;
}

// Check for Optical Character Recognition (OCR)
if (selectedOptions.includes("Summary")) {
  const summaryFormat = "Provide a detailed summary of the key events, scenes, and main content of the video";
  outputFormats.summary = summaryFormat;
}

// Check for Description
if (selectedOptions.includes("Keywords")) {
  const keywordsFormat = [
  "Generate a list of most relevant keywords or topics related to the video content"
  ];
  outputFormats.keywords = keywordsFormat;
}

// Check for Video Captioning 
