  const actionRecognitionFormat = [
    {
      "timestamp": "[Time in the video, e.g., '00:02:05']",
      "action": "[Detected action, e.g., 'running', 'jumping', etc...]",
      "subject": "[Who is performing the action, e.g., 'man', 'child']",
