const selectedOptions = msg.selectedOptions;
const allFormats = msg.payload.formats || {};
const outputFormats = {};

// Build output based on selected options
for (const option of selectedOptions) {
    if (allFormats[option]) {
        const key = option.toLowerCase().replace(/ /g, "");
        outputFormats[key] = allFormats[option];
    }
}

// Always add noResultsFormat
msg.noResultsFormat = JSON.stringify(msg.payload.noResultsFormat || [], null, 2);
msg.formats = JSON.stringify(outputFormats, null, 2);

return msg;

