/**
 * Gemini â€“ robust listFiles (JSON header + exp-back-off) with Debug
 *
 * OUTPUT 1 : msg.files    â€“ array (may be empty)
 * OUTPUT 2 : msg          â€“ error path
 * Additional Debug Output: exact API request details
 */

if (!msg.apiKey || typeof msg.apiKey !== "string") {
  node.error("Gemini List: msg.apiKey missing or invalid", msg);
  return [null, msg];
}

const fetchFn = globalThis.fetch || nodeFetch;

const MAX_ATTEMPTS = 12;
const BASE_DELAY = 1000;

function sleep(ms) { return new Promise(r => setTimeout(r, ms)); }

async function fetchOnePage(key, pageToken) {
  const url =
    "https://generativelanguage.googleapis.com/v1beta/files?" +
    "key=" + encodeURIComponent(key) +
    "&pageSize=100" +
    (pageToken ? "&pageToken=" + encodeURIComponent(pageToken) : "");

  // Debugging: output the exact request details
  node.send({ debugRequest: { url, headers: { "accept": "application/json" } } });

  const res = await fetchFn(url, { headers: { "accept": "application/json" } });
  const text = await res.text();

  if (res.ok && text.trim().startsWith("{")) {
    return JSON.parse(text);
  }

  if (res.status === 500 || text.includes("Failed to convert server response")) {
    throw new Error("RETRY");
  }

  throw new Error(`HTTP ${res.status}: ${text.slice(0, 120)}`);
}

async function listAllFiles(key) {
  let files = [], pageToken, attempt = 0;

  do {
    let success = false, body;
    while (!success && attempt < MAX_ATTEMPTS) {
      try {
        body = await fetchOnePage(key, pageToken);
        success = true;
      } catch (e) {
        if (e.message === "RETRY") {
          attempt++;
          const wait = BASE_DELAY * Math.pow(2, attempt);
          node.warn(`listFiles 500 â€“ retry ${attempt}/${MAX_ATTEMPTS} in ${wait / 1000}s`);
          await sleep(wait);
        } else {
          throw e;
        }
      }
    }

    if (!success) {
      throw new Error("listFiles gave up after repeated 500s");
    }

    files.push(...(body.files || []));
    pageToken = body.nextPageToken;

  } while (pageToken);

  return files;
}

if (!msg.apiKey || typeof msg.apiKey !== "string") {
  node.error("Gemini List: msg.apiKey missing or invalid", msg);
  return [null, msg];
}

return listAllFiles(msg.apiKey)
  .then(arr => {
    msg.files = arr;
    return [msg, null];
  })
  .catch(err => {
    node.error(`listFiles failed: ${err}`, msg);
    return [null, msg];
  });

