// â”€â”€â”€ âº  Perform the upload asynchronously â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
return (async () => {
  try {
    const bucket = storage.bucket(bucketName);
    const file = bucket.file(destFilename);

    // Set MIME type to video/mp4 (adjust if your file is different)
    const writeStream = file.createWriteStream({
      metadata: {
        contentType: "video/mp4"
      },
      // If your file is very large, set resumable: true
      resumable: false
    });

    // Push the Buffer into GCS
    writeStream.end(videoBuffer);

    // Await completion
    await new Promise((resolve, reject) => {
      writeStream.on("finish", resolve);
      writeStream.on("error", reject);
    });

    // Attach the GCS URI for downstream nodes
    msg.gcsUri = `gs://${bucketName}/${destFilename}`;

    // (Optionally) delete the in-memory Buffer if you no longer need it:
    // delete msg.payload;

    return msg;
  }
  catch (err) {
    node.error("âŒ Upload to GCS failed: " + err.message, msg);
    return null;
  }
})();

